@tailwind base;
@tailwind components;
@tailwind utilities;

@layer utilities {
  .scrollbar-hide::-webkit-scrollbar {
    display: none;
  }

  .scrollbar-hide {
    -ms-overflow-style: none;
    scrollbar-width: none;
  }
}

:root {
  --background: hsl(0, 0%, 100%);
  --foreground: hsl(0, 0%, 12%);
  --muted: hsl(210, 40%, 96%);
  --muted-foreground: hsl(215, 16%, 47%);
  --popover: hsl(0, 0%, 100%);
  --popover-foreground: hsl(0, 0%, 12%);
  --card: hsl(0, 0%, 100%);
  --card-foreground: hsl(0, 0%, 12%);
  --border: hsl(214, 32%, 91%);
  --input: hsl(214, 32%, 91%);
  --primary: hsl(209, 85%, 18%);
  --primary-foreground: hsl(0, 0%, 100%);
  --secondary: hsl(210, 40%, 96%);
  --secondary-foreground: hsl(0, 0%, 12%);
  --accent: hsl(210, 40%, 96%);
  --accent-foreground: hsl(0, 0%, 12%);
  --destructive: hsl(0, 84.2%, 60.2%);
  --destructive-foreground: hsl(0, 0%, 100%);
  --ring: hsl(209, 85%, 18%);
  --radius: 0.5rem;

  /* Mamta Steel Custom Color Palette */
  --navy-primary: hsl(209, 85%, 18%);
  --navy-secondary: hsl(213, 59%, 29%);
  --gold-primary: hsl(36, 95%, 51%);
  --gold-hover: hsl(34, 89%, 47%);
  --text-light: hsl(0, 0%, 100%);
  --text-gray: hsl(0, 0%, 88%);

  /* Legacy steel colors for backward compatibility */
  --steel-blue-50: hsl(214, 100%, 97%);
  --steel-blue-500: hsl(209, 85%, 18%);
  --steel-blue-600: hsl(213, 59%, 29%);
  --steel-blue-700: hsl(213, 59%, 29%);
  --steel-blue-800: hsl(209, 85%, 18%);
  --steel-blue-900: hsl(209, 85%, 18%);
  --steel-orange-500: hsl(36, 95%, 51%);
  --steel-orange-600: hsl(34, 89%, 47%);
  --steel-orange-700: hsl(32, 84%, 42%);
}

.dark {
  --background: hsl(209, 85%, 18%);
  --foreground: hsl(0, 0%, 98%);
  --muted: hsl(213, 59%, 29%);
  --muted-foreground: hsl(0, 0%, 88%);
  --popover: hsl(209, 85%, 18%);
  --popover-foreground: hsl(0, 0%, 98%);
  --card: hsl(213, 59%, 29%);
  --card-foreground: hsl(0, 0%, 98%);
  --border: hsl(213, 59%, 29%);
  --input: hsl(213, 59%, 29%);
  --primary: hsl(36, 95%, 51%);
  --primary-foreground: hsl(209, 85%, 18%);
  --secondary: hsl(213, 59%, 29%);
  --secondary-foreground: hsl(0, 0%, 98%);
  --accent: hsl(213, 59%, 29%);
  --accent-foreground: hsl(0, 0%, 98%);
  --destructive: hsl(0, 62.8%, 30.6%);
  --destructive-foreground: hsl(0, 0%, 98%);
  --ring: hsl(36, 95%, 51%);
  --radius: 0.5rem;

  /* Dark mode custom colors */
  --navy-primary: hsl(213, 59%, 29%);
  --navy-secondary: hsl(209, 85%, 18%);
  --gold-primary: hsl(36, 95%, 51%);
  --gold-hover: hsl(34, 89%, 47%);
}

/* Custom Swiper Styles for Industry Carousel */
.industry-swiper .swiper-pagination {
  bottom: 0 !important;
}

* {
  user-select: none;
  -webkit-user-select: none;
  /* Safari ke liye */
  -ms-user-select: none;
  /* Internet Explorer ke liye */
}


.industry-swiper .swiper-pagination-bullet {
  width: 12px;
  height: 12px;
  background: #cbd5e1;
  opacity: 0.7;
  transition: all 0.3s ease;
}

.industry-swiper .swiper-pagination-bullet-active-custom {
  background: #f39c12 !important;
  opacity: 1;
  transform: scale(1.2);
}

.industry-swiper .swiper-pagination-bullet:hover {
  background: #f39c12;
  opacity: 0.8;
}

/* Shimmer Animation for Section Dividers */
@keyframes shimmer {
  0% {
    transform: translateX(-100%);
  }

  100% {
    transform: translateX(100%);
  }
}

.shimmer-gradient {
  position: relative;
  overflow: hidden;
}

.shimmer-gradient::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.4), transparent);
  animation: shimmer 3s infinite;
}

/* Footer Animated Divider */
.footer-divider {
  height: 1px;
  background: linear-gradient(to right, transparent, #f39c12, transparent);
  margin: 0 auto 20px;
  width: 80%;
  max-width: 600px;
}

/* Footer Modern Animations */
@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(30px);
  }

  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes glow {

  0%,
  100% {
    box-shadow: 0 0 5px rgba(249, 115, 22, 0.2);
  }

  50% {
    box-shadow: 0 0 20px rgba(249, 115, 22, 0.4);
  }
}

.footer-animate {
  animation: fadeInUp 0.8s ease-out;
}

.footer-glow:hover {
  animation: glow 2s infinite;
}

/* Enhanced footer social icon animations */
@keyframes bounce-gentle {

  0%,
  100% {
    transform: translateY(0) rotate(0deg) scale(1);
  }

  50% {
    transform: translateY(-5px) rotate(5deg) scale(1.1);
  }
}

.social-bounce:hover {
  animation: bounce-gentle 0.6s ease-in-out;
}

/* Divider line gradient animation */
@keyframes gradient-shift {
  0% {
    background-position: 0% 50%;
  }

  50% {
    background-position: 100% 50%;
  }

  100% {
    background-position: 0% 50%;
  }
}

.gradient-divider {
  background: linear-gradient(-45deg, #facc15, #ef4444, #f97316, #facc15);
  background-size: 400% 400%;
  animation: gradient-shift 4s ease infinite;
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    font-family: 'Poppins', sans-serif;
    overflow-x: hidden;
  }
}

@layer utilities {

  /* Custom font sizes as per requirement */
  .text-heading {
    font-size: 28px;
    line-height: 1.2;
  }

  /* Hide scrollbar for Chrome, Safari and Opera */
  .overflow-x-auto::-webkit-scrollbar {
    display: none;
  }

  /* Hide scrollbar for IE, Edge and Firefox */
  .overflow-x-auto {
    -ms-overflow-style: none;
    scrollbar-width: none;
  }
}

@layer components {
  .steel-blue-50 {
    background-color: var(--steel-blue-50);
  }

  .steel-blue-500 {
    background-color: var(--steel-blue-500);
  }

  .steel-blue-600 {
    background-color: var(--steel-blue-600);
  }

  .steel-blue-700 {
    background-color: var(--steel-blue-700);
  }

  .steel-blue-800 {
    background-color: var(--steel-blue-800);
  }

  .steel-blue-900 {
    background-color: var(--steel-blue-900);
  }

  .steel-orange-500 {
    background-color: var(--steel-orange-500);
  }

  .steel-orange-600 {
    background-color: var(--steel-orange-600);
  }

  .steel-orange-700 {
    background-color: var(--steel-orange-700);
  }

  .text-steel-blue-800 {
    color: var(--steel-blue-800);
  }

  .text-steel-orange-400 {
    color: var(--steel-orange-500);
  }

  .text-steel-orange-500 {
    color: var(--steel-orange-500);
  }

  .hover\:text-steel-orange-500:hover {
    color: var(--steel-orange-500);
  }

  .hover\:bg-steel-orange-600:hover {
    background-color: var(--steel-orange-600);
  }

  .hover\:bg-steel-blue-700:hover {
    background-color: var(--steel-blue-700);
  }
}

/* Swiper customization */
.swiper-button-next,
.swiper-button-prev {
  color: var(--steel-orange-500) !important;
}

.swiper-pagination-bullet-active {
  background-color: var(--steel-orange-500) !important;
}

