client/src/components/Navbar.tsx.content:
import { useState, useEffect, useRef } from "react";
import { Link, useLocation } from "wouter";
import { 
  Home, 
  Info, 
  Settings, 
  Wrench, 
  Award, 
  Mail, 
  Menu, 
  X, 
  ChevronDown,
  ChevronRight,
  FileText,
  Cylinder,
  RectangleHorizontal,
  BarChart3,
  Puzzle,
  Circle,
  Gauge,
  BookOpen,
  TestTube,
  Layers,
  TrendingUp,
  Zap,
  Bolt,
  Hammer,
  Pin,
  Flashlight
} from "lucide-react";
import logoImage from "@assets/logomamtasteelwithoutbg_1760247842336.png";

export default function Navbar() {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [mobileProductsOpen, setMobileProductsOpen] = useState(false);
  const [mobileTechnicalInfoOpen, setMobileTechnicalInfoOpen] = useState(false);
  const [mobileSubmenus, setMobileSubmenus] = useState({
    pipes: false,
    sheets: false,
    bars: false,
    coldWorkToolSteels: false,
    fittings: false,
    flanges: false,
    fasteners: false,
    weldingElectrodes: false,
    galvanized: false,
    pins: false
  });
  // Desktop dropdown states
  const [isProductsDropdownOpen, setIsProductsDropdownOpen] = useState(false);
  const [isTechnicalDropdownOpen, setIsTechnicalDropdownOpen] = useState(false);
  const dropdownRef = useRef<HTMLDivElement>(null);
  const technicalDropdownRef = useRef<HTMLDivElement>(null);
  const [location] = useLocation();

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
    // Reset all submenu states when closing mobile menu
    if (isMobileMenuOpen) {
      setMobileProductsOpen(false);
      setMobileTechnicalInfoOpen(false);
      setMobileSubmenus({
        pipes: false,
        sheets: false,
        bars: false,
        coldWorkToolSteels: false,
        fittings: false,
        flanges: false,
        fasteners: false,
        weldingElectrodes: false,
        galvanized: false,
        pins: false
      });
    }
  };

  const toggleMobileProducts = () => {
    setMobileProductsOpen(!mobileProductsOpen);
    // Close all submenus when toggling main products menu
    setMobileSubmenus({
      pipes: false,
      sheets: false,
      bars: false,
      coldWorkToolSteels: false,
      fittings: false,
      flanges: false,
      fasteners: false,
      weldingElectrodes: false,
      galvanized: false,
      pins: false
    });
  };

  const toggleMobileSubmenu = (submenu: keyof typeof mobileSubmenus) => {
    setMobileSubmenus(prev => ({
      ...prev,
      [submenu]: !prev[submenu]
    }));
  };

  const toggleMobileTechnicalInfo = () => {
    setMobileTechnicalInfoOpen(!mobileTechnicalInfoOpen);
  };

  const isActive = (path: string) => {
    return location === path;
  };

  // Close dropdowns when clicking outside
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target as Node)) {
        setIsProductsDropdownOpen(false);
      }
      if (technicalDropdownRef.current && !technicalDropdownRef.current.contains(event.target as Node)) {
        setIsTechnicalDropdownOpen(false);
      }
    };

    const handleEscapeKey = (event: KeyboardEvent) => {
      if (event.key === 'Escape') {
        setIsProductsDropdownOpen(false);
        setIsTechnicalDropdownOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    document.addEventListener('keydown', handleEscapeKey);

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
      document.removeEventListener('keydown', handleEscapeKey);
    };
  }, []);

  // Close dropdown when navigating
  useEffect(() => {
    setIsProductsDropdownOpen(false);
    setIsTechnicalDropdownOpen(false);
  }, [location]);

  const toggleProductsDropdown = () => {
    setIsProductsDropdownOpen(!isProductsDropdownOpen);
    setIsTechnicalDropdownOpen(false); // Close other dropdown
  };

  const toggleTechnicalDropdown = () => {
    setIsTechnicalDropdownOpen(!isTechnicalDropdownOpen);
    setIsProductsDropdownOpen(false); // Close other dropdown
  };

  return (
    <nav className="bg-navy-secondary text-white sticky top-0 z-50 shadow-lg">
      <div className="w-full max-w-7xl mx-auto px-2 sm:px-3 lg:px-4 xl:px-6">
        <div className="flex items-center justify-between h-16">
          {/* Logo (Mobile) */}
          <div className="flex items-center md:hidden">
            <img src={logoImage} alt="Mamta Steel Traders Logo" className="h-10 w-auto object-contain" />
          </div>

          {/* Desktop Navigation - Centered */}
          <div className="hidden md:flex items-center justify-center flex-1 space-x-3 lg:space-x-6 xl:space-x-8">
            <Link href="/" className={`hover:text-gold-primary transition-colors flex items-center ${isActive('/') ? 'text-gold-primary' : ''}`} data-testid="link-home">
              <Home className="w-4 h-4 mr-2" />
